import React, { useState } from "react";
import "../styles/login.css";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";

function Login() {
    const [username, setUsername] = useState(null);
    const [password, setPassword] = useState(null);

    const dispatch = useDispatch();

    const checkName = (value) => {
        const regexp = /^[a-zA-Z]{3,}$/gm;
        if (regexp.test(value)) {
            setUsername(true);
        } else {
            setUsername(false);
        }
    };

    const checkPass = (value) => {
        const regexp = /[a-zA-Z\d]{5,}/gm;
        if (regexp.test(value)) {
            setPassword(true);
        } else {
            setPassword(false);
        }
    };
    const logIn = (target) => {
        console.log(target.elements["username"].value);
        localStorage.setItem("username", target.elements["username"].value);
        dispatch({ type: "login", data: target.elements["username"].value });
    };

    return (
        <div className="login">
            <h1 className="login-title">Login</h1>
            <form
                className="form"
                method="post"
                action="#"
                onSubmit={(e) => logIn(e.target)}
            >
                <label className="login_label">Username:</label>
                <input
                    className="login_input"
                    placeholder="Username"
                    name="username"
                    type="text"
                    onChange={(e) => checkName(e.target.value)}
                ></input>
                <span
                    className={
                        username === false ? "error display-error" : "error"
                    }
                >
                    Username is incorrect (only 3+ letters)
                </span>
                <label className="login_label">Password:</label>
                <input
                    className="login_input"
                    placeholder="Password"
                    name="password"
                    type="password"
                    onChange={(e) => checkPass(e.target.value)}
                ></input>
                <span
                    className={
                        password === false ? "error display-error" : "error"
                    }
                >
                    Password is incorrect (must be 5 and more symbols)
                </span>
                <div className="btn-container">
                    <button
                        className={
                            !(username && password) ? "btn" : "btn show-btn"
                        }
                        type="submit"
                        onClick={() => logIn()}
                    >
                        {/* <Link to="/">Go!</Link> */}
                    </button>
                </div>
            </form>
        </div>
    );
}

export default Login;
