import React, { useState } from "react";
import "../styles/login.css";
import { Link } from "react-router-dom";

function Login() {
    const [username, setUsername] = useState(null);
    const [password, setPassword] = useState(null);

    const checkName = (value) => {
        const regexp = /^[a-zA-Z]{3,}$/gm;
        if (regexp.test(value)) {
            setUsername(true);
        } else {
            setUsername(false);
        }
    };

    const checkPass = (value) => {
        const regexp = /[a-zA-Z\d]{5,}/gm;
        if (regexp.test(value)) {
            setPassword(true);
        } else {
            setPassword(false);
        }
    };
    const logIn = () => {
        console.log("btn clivk!");
        window.location.assign("/home");
    };

    return (
        <div className="login">
            <h1 className="login-title">Login</h1>
            <form className="form">
                <label className="login_label">Username:</label>
                <input
                    className="login_input"
                    placeholder="Username"
                    onChange={(e) => checkName(e.target.value)}
                ></input>
                <span
                    className={
                        username === false ? "error display-error" : "error"
                    }
                >
                    Username is incorrect (only 3+ letters)
                </span>
                <label className="login_label">Password:</label>
                <input
                    className="login_input"
                    placeholder="Password"
                    onChange={(e) => checkPass(e.target.value)}
                ></input>
                <span
                    className={
                        password === false ? "error display-error" : "error"
                    }
                >
                    Password is incorrect (must be 5 and more symbols)
                </span>
                <div className="btn-container">
                    <button
                        className="btn"
                        disabled={!(username && password)}
                        onClick={(e) => logIn(e)}
                    >
                        <Link to="/home">Go!</Link>
                    </button>
                </div>
            </form>
        </div>
    );
}

export default Login;
